import router from "../../services/fetch";
import { checkString } from "../../utils";
export async function getBio(author) {
    try {
        if (!author || checkString(author)) {
            throw new Error("essa query não é válido.");
        }
        const { data } = await router.get(`/autor/${author.toLowerCase().replace(" ", "_")}/biografia`);
        return { html: data };
    }
    catch (error) {
        return { err: error.message };
    }
}
